$(function(){if($("#search_word").html()===""){$("#content_post").append('<div class="h3">No results</div>')}else{$.ajax({url:"/re/search/post/",type:"post",dataType:"json",cache:false,data:{search_word:$("#search_word").html(),next_id:$("#next_id").html()},success:function(data){if(data.res===1){if(data.post_set.length===0){$("#content_post").append('<div class="h3">No results</div>')}else{$.each(data.post_set,function(key,value){var appender='<div class="row div_base" id="post_div_'+value.id+'">'+"<script defer>"+'    profile_populate("'+value.id+'")'+"<"+"/script></div>";$("#content_post").append(appender)})}if(data.post_next===null){$("#more_post_load").addClass("hidden");$("#next_id").html("")}else{$("#more_post_load").removeClass("hidden");$("#next_id").html(data.post_next)}}}})}$("#more_post_load").on("click",function(e){e.preventDefault();$.ajax({url:"/re/search/post/",type:"post",dataType:"json",cache:false,data:{search_word:$("#search_word").html(),next_id:$("#next_id").html()},success:function(data){if(data.res===1){if(data.post_set.length===0){$("#content_post").append('<div class="h3">No results</div>')}else{$.each(data.post_set,function(key,value){var appender='<div class="row div_base" id="post_div_'+value.id+'">'+"<script defer>"+'    profile_populate("'+value.id+'")'+"<"+"/script></div>";$("#content_post").append(appender)})}if(data.post_next===null){$("#more_post_load").addClass("hidden");$("#next_id").html("")}else{$("#more_post_load").removeClass("hidden");$("#next_id").html(data.post_next)}}}})})});